import React,{Component} from 'react'
import {getBanner,getBanner2,getHotContent,getDayContent} from './model.js'
import Footer from '../../components/footer'
import './index.scss'

class Index extends Component{
	constructor(props) {
	  super(props);
	  this.state = {
	  	bannerList:[],
	  	bannerList2:{},
	  	contentDay:[],
	  	contentHot:[]
	  };
	}

	componentWillMount(){
		getBanner().then(res=>
			this.setState({
				bannerList:res
			})
		);
		getBanner2().then(res=>
			this.setState({
				bannerList2:res
			})
		)
		getDayContent().then(res=>
			// console.log(res)
			this.setState({
				contentDay:res
			})
		)
		getHotContent().then(res=>{
			this.setState({
				contentHot:res
			})
		})
	}
	render(){
		return <div id="indexf">
		{
			this.state.bannerList.map(item=>{
				return <div key={item.id} className="banner">
					<img src={item.main_image}/>
					<div className="title">
						<h2>{item.main_title}</h2>
						<span>{item.sub_title}</span>
					</div>
				</div>
			})
		}
		{
			this.state.bannerList2.length !== 0?
			<div className="banner">
				<img src={this.state.bannerList2.img} onClick={this.handleGift.bind(this)}/>
			</div>
			:null
		}
		<div className="content">
			<img src="data:image/png;base64," className="hotday"/>
			<ul>
			{
				this.state.contentDay.map((item,index)=>{
					return <li key={item.eventId} onClick={this.handleDetail.bind(this,item.eventId)}>
						<img src={item.imageUrl} alt="图片出不来了"/>
						<div>
							<span>{item.englishName}</span>
							<p>{item.chineseName}</p>
							<p>{item.discountText}</p>
						</div>
					</li>
				})
			}
			</ul>
			<img src="data:image/png;base64," className="hotday"/>
			<ul>
			{
				this.state.contentHot.map((item,index)=>{
					return <li key={item.eventId} onClick={this.handleDetail.bind(this,item.eventId)}>
						<img src={item.imageUrl} alt="图片出不来了"/>
						<div>
							<span>{item.englishName}</span>
							<p>{item.chineseName}</p>
							<p>{item.discountText}</p>
						</div>
					</li>
				})
			}
			</ul>
			<div className="moreEven">
				<img src="data:image/png;base64," className="more"/>
				<img src="http://www.mei.com/static/img/pinpai.f14a967.png" onClick={this.handleTrademark.bind(this)}/>
				<img src="http://www.mei.com/static/img/jjsx.d717264.png" className="right"  onClick={this.handleUp.bind(this)}/>
			</div>
		</div>
		<Footer></Footer>
		</div>
	}
	handleGift(){
		this.props.history.push(`/gift`)
	}
	handleUp(){
		this.props.history.push(`/upcoming`)
	}
	handleTrademark(){
		this.props.history.push(`/trademark`)
	}
	handleDetail(id){
		this.props.history.push(`/productmini/${id}`)
	}
}
                                                                                                                                      
export default Index
